module.exports = {
  createNpmDependenciesGraph: createNpmDependenciesGraph
};

function createNpmDependenciesGraph(pkgId, graph) {
  graph.beginUpdate();
  data.nodes.forEach(function (node) {
    graph.addNode(node.id, node.data);
  });
  data.links.forEach(function (link) {
    graph.addLink(link.fromId, link.toId);
  });
  graph.endUpdate();
  return {
    then: function (cb) {
      setTimeout(function () {
        cb(graph);
      });
    }
  };
}
